mapper.select:
SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM order_users
		
			WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'
		
		ORDER BY order_usersno DESC
	)Tb
)
WHERE rNum>=#{start}
AND rNum<=#{end}

SELECT * FROM (
SELECT Tb.*, rownum rNum FROM (
SELECT * FROM product

	WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'

ORDER BY productno DESC
)Tb
)
WHERE rNum>=#{start}
AND rNum<=#{end}

