mapper.select:
SELECT * FROM (
	SELECT Tb.*, rownum rNum FROM (
		SELECT * FROM product

		WHERE ${searchField} LIKE '%'||#{searchKeyword}||'%'

		ORDER BY productno DESC
	)Tb
)
WHERE rNum>=#{start} AND rNum<=#{end}

mapper.update:
UPDATE product SET
	productCode=#{productCode}, productName=#{productName}, productName_En=#{productName_En}, state=#{state}, productInfo=#{productInfo}, 
	productImg=#{productImg}, fullPrice=#{fullPrice}, discountPrice=#{discountPrice}, stock=#{stock}, 
	discountRate=#{discountRate}, wine=#{wine}, country=#{country}, grapeVariety=#{grapeVariety}, alcohol=#{alcohol}, body=#{body},
 	acidity=#{acidity}, tannins=#{tannins}, sweetNess=#{sweetNess}
 	WHERE productNo=#{productNo}
